$(document).ready(function(){var formElement=$('#__FORMNAME__');var submitElement=$('[data-identifier="submit"]',formElement);var spinnerElement=$('[data-identifier="spinner"]',formElement);submitElement.click(function(e){e.preventDefault();var formData=$(this).closest('form').serializeArray();formData.push({name:this.name,value:this.value});var ts=Math.round(+new Date());var messageTagId='contact-form-message'+ts;spinnerElement.fadeIn(50);submitElement.attr('disabled','disabled');$.ajax({type:'POST',url:formElement.attr('action')+'?type=6171240&tx_pxanewslettersubscription_subscription%5Baction%5D=ajax',data:formData,dataType:"JSON",success:function(response){formElement.after('<div id="'+messageTagId+'" class="alert">'+response.message+'<div>');if (response.success){$('#'+messageTagId).addClass('alert-success');formElement.hide();} else {$('#' + messageTagId).addClass('alert-danger').delay(5000).fadeOut('slow');}spinnerElement.fadeOut(50);submitElement.removeAttr('disabled');},error:function(jqXHR,textStatus,errorThrown){formElement.after('<div id="'+messageTagId+'" class="alert alert-danger">'+jqXHR_error_message+'<div>');$('#'+messageTagId).delay(5000).fadeOut('slow');spinnerElement.fadeOut(50);submitElement.removeAttr('disabled');}});});});