$(document).ready(function(){var formElement=$("#__FORMNAME__");var submitElement=$('[data-identifier="submit"]',formElement);var spinnerElement=$('[data-identifier="spinner"]',formElement);submitElement.click(function(e){e.preventDefault();var formData=$(this).closest("form").serializeArray();formData.push({name:this.name,value:this.value});var ts=Math.round(+new Date);var messageTagId="contact-form-message"+ts;spinnerElement.fadeIn(50);submitElement.attr("disabled","disabled");$(".js__ajax-response").hide();$.ajax({type:"POST",url:formElement.attr("action"),data:formData,dataType:"JSON",success:function(response){formElement.after('<div id="'+messageTagId+'" class="alert js__ajax-response">'+response.message+"<div>");if(response.success){$("#"+messageTagId).addClass("alert-success");formElement.hide()}else{$("#"+messageTagId).addClass("alert-danger").delay(5e3).fadeOut("slow")}spinnerElement.fadeOut(50);submitElement.removeAttr("disabled")},error:function(jqXHR,textStatus,errorThrown){formElement.after('<div id="'+messageTagId+'" class="alert alert-danger js__ajax-response">'+jqXHR_error_message+"<div>");$("#"+messageTagId).delay(5e3).fadeOut("slow");spinnerElement.fadeOut(50);submitElement.removeAttr("disabled")}})})});
